const CampaignStatus = ({campaign}) => {
  return(
    <div>
      {
        campaign.status === "Not Started" ? <td><p className="col-data copy__para--medium">{campaign.status}</p></td> : 
        <div className="status__dropdown">
          <td>
            <div>
            <button className="btn-sec">{campaign.status}</button>
            </div>
          </td>
          <div className="status__dropdown--items">
            {
            campaign.status === "Active" ? 
              <p>Pause</p> : 
              <p>Resume</p>
            }
          </div>
        </div>
      }
    </div>
  )
}

export default CampaignStatus;
