import axios from "axios";
import Cookies from "universal-cookie";
import {useEffect, useState} from "react"

const cookies = new Cookies()
function AppTopbar(props){
  const [user, setUser] = useState({})
  useEffect(() => {
    getUser()
  })
  const getUser = () => {
    const userId = cookies.get("userId")
    axios.get(`/users/${userId}`, {
      headers: {
        "Authorization": `Bearer ${cookies.get('access_token')}`
      }
    }).then((res) => {
      // if(isEmpty(user)){
      //   console.log(user)
      // }
      setUser(res.data)
    })
  }

  return(
    <nav className="app-topbar">
      <div className="app-topbar__container">
        <div className="app-topbar__title"><h4 className="heading-2">{props.title}</h4></div>

        <div className="app-topbar__options">
          <div className="app-topbar__options--profile">
            {/* <button className="button copy__para--medium">{Object.keys(user) !== 0 ? user.username.charAt(0).toUpperCase() : null}</button> */}
            <div className="menu" style={{display: "none"}}>
              <div className="menu__content">
                <div className="menu__content--option copy__para--medium" onClick={null}><i className="fas fa-edit"></i>Edit Campaign Name</div>
                <div className="menu__content--option copy__para--medium" onClick={null}><i className="fas fa-trash-alt"></i>Delete</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </nav>
  )
}

export default AppTopbar;
