import {useEffect} from "react"

const DelayDropdown = ({index, day, stepNumber}) => {
  useEffect(() => {
  }, [])

  const toggleDropdown = () => {
    let el = document.getElementsByClassName("dropdown__container--content")[index]
    console.log(el)
    if(el.style.display === "block"){
      el.style.display = "none";
    }
    else{
      el.style.display = "block";
    }
  }
  if(index !== 0) {
    return(
      <div className="dropdown__container">
                      <h3 key={index} className="heading-2">Step {d.stepNumber}, Day {d.day}</h3>
        <h3 className="heading-2">
          <span> Step {stepNumber}, </span> 
          <button className="btn-sec" onClick={toggleDropdown}>Day {day} <i className="fas fa-caret-down"></i></button>
        </h3>
        <div className="dropdown__container--content" style={{display: "none"}}>
          <h3 className="heading-2">Schedule after a delay of</h3>

          <div className="dropdown__container--content--items">
            <div>
              <input type="number" id="days"/>
              <label className="copy__para--medium" htmlFor="days">days</label>
            </div>
            <div>
              <input type="number" id="hours"/>
              <label className="copy__para--medium" htmlFor="hours">hours</label>
            </div>
            <div>
              <input type="number" id="minute"/>
              <label className="copy__para--medium" htmlFor="minute">minutes</label>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default DelayDropdown;
