import Sidebar from "../../Components/Sidebar"
import Topbar from "../../Components/Topbar"
import ProspectSidebar from "../../Components/ProspectSidebar"
import {useState , useContext, useEffect} from "react"
import { GlobalContext } from "../../GlobalProvider"
import Add from "../../Components/Prospects/Add"
import axios from "axios"
import Cookies from 'universal-cookie';

const cookies = new Cookies()
function ProspectsHome(){
  const {addProspect} = useContext(GlobalContext)
  const [add, setAdd] = addProspect
  const [showProspects, setShowProspects] = useState([])

  function toggleAdd(){
    setAdd(!add)
  }

  useEffect(() => {
    getListData()
  }, [])

  const getListData = async () => {
    const userId = cookies.get("userId")
    await axios.get("/prospects/prospect/user", {
      headers: {
        "Authorization": `Bearer ${cookies.get('access_token')}`
      },
      params: {
        userId: userId
      }
    }).then((res) => {
      if(res.data === ""){
        console.log("Data Null")
      }
      else{
        console.log(res.data.prospects)
        setShowProspects(res.data.prospects)
      }
    })
  }

  return(
    <div className="prospects">
      {add===true ? <Add/> : console.log('null')}
      <Sidebar/>
      <Topbar title={"Prospects"}/>
        <div className="prospects__container">
          <div className="prospects__container--sidebar">
            <ProspectSidebar/>
          </div>

          <div className="prospects__container--content">
            <div className="toolbar">
              <div className="name">
                <h4>My Prospects</h4>
              </div>
              <div className="search">
                <input type="search" placeholder="Search"/>
                <button onClick={toggleAdd} className="cta">Add Prospects</button>
              </div>
            </div>

            <div className="table__container">
              <table className="data">
                <thead>
                  <tr className="row1">
                    <th className="col-head">Prospect</th>
                    <th className="col-head">Contact</th>
                    <th className="col-head">Company</th>
                    <th className="col-head">List</th>
                    <th className="col-head">Tags</th>
                    <th className="col-head">Status</th>
                  </tr>
                </thead>

                <tbody>
                  {showProspects.map(d => 
                    <tr key={d.id}>
                      <td><p className="col-data">{d.firstName} {d.lastName}</p></td>
                      <td><p className="col-data">
                            {d.prospectLinkedinUrl}
                            {d.prospectMobileNumber}
                            {d.prospectEmail}
                      </p></td>
                      <td><p className="col-data">{d.prospectCompany}</p></td>
                      <td><p className="col-data">Campaign 1</p></td>
                      <td><p className="col-data">-</p></td>
                      <td><p className="col-data">Not Contacted</p></td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
   </div>
  )
}

export default ProspectsHome
