import Sidebar from "../../Components/Sidebar"
import Topbar from "../../Components/Topbar"
import ProspectSidebar from "../../Components/ProspectSidebar"
import {useState , useContext, useEffect} from "react"
import { GlobalContext } from "../../GlobalProvider"
import AddList from "../../Components/Prospects/AddList"
import axios from "axios"
import Cookies from 'universal-cookie';

const cookies = new Cookies()

function ProspectLists(){
  const {createList} = useContext(GlobalContext)
  const [list, setList] = createList
  const [showLists, setShowLists] = useState([])

  useEffect(() => {
    getListData()
  }, [])

  function toggleList(){
    setList(!list)
  }

  const getListData = async () => {
    const userId = cookies.get("userId")
    await axios.get(`/prospects/prospect_lists/list?userId=${userId}`, {
      headers: {
        "Authorization": `Bearer ${cookies.get('access_token')}`
      }
    }).then((res) => {
      setShowLists(res.data)
    })
  }

  return(
    <div className="prospect-lists">
      {list===true ? <AddList/> : console.log('null')}
      <Sidebar/>
      <Topbar title={"Prospects"}/>
      <h1>Hello</h1>

      <div className="prospect-lists__container">
        <div className="prospect-lists__container--sidebar">
            <ProspectSidebar/>
        </div>
          <div className="prospect-lists__container--content">
            <div className="toolbar">
              <div className="toolbar__email">
                <p className="toolbar__email--card">Owner: singhrathoremehul@gmail.com</p>
                <p className="times">&times;</p>
              </div>
              <div className="search">
                <input type="search" placeholder="Search"/>
                <button onClick={toggleList} className="cta">Create List</button>
              </div>
            </div>

            <div className="table__container">
              <table className="data">
                <thead>
                  <tr className="row1">
                    <th className="col-head">List Name</th>
                    <th className="col-head">All</th>
                    <th className="col-head">Not Contacted</th>
                    <th className="col-head">Actions</th>
                  </tr>
                </thead>

                <tbody>
                  {showLists.map(d => 
                    <tr>
                      <td><p className="col-data" key={d.id}>{d.name}</p></td>
                      <td><p className="col-data">Campaign 1</p></td>
                      <td><p className="col-data">-</p></td>
                      <td><p className="col-data">Not Contacted</p></td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
      </div>
    </div>
  )
}

export default ProspectLists
