import {useState , useContext} from "react"
import { GlobalContext } from "../../GlobalProvider"
import axios from "axios"
import Cookies from 'universal-cookie';

const cookies = new Cookies();
function UpdateName({id}) {
  const {updateCampaign, selectedCampaign} = useContext(GlobalContext)
  const [modal, setModal] = updateCampaign
  const [name, setName] = useState('')
  const [campaignId, setCampaignId] = selectedCampaign

  function toggleList(){
    setModal(!modal)
  }

  const addList = () => {
    const token = cookies.get("access_token")
    const data = {
      campaign_name: name
    }
    const params = {
      campaignId: campaignId
    }
    axios.put(`/campaigns/name`, data, {
      headers:{
        "Authorization": `Bearer ${token}`
      },
      params: params
    }).then((res) => {
      if(res.status === 201){
        window.location.reload()
      }
    })
  }

  return(
    <div className="add-list">
      <div className="add-list__card">
        <h1 className="u-margin-top-small u-margin-bottom-small">Create List</h1>
        <div className="add-list__form u-margin-bottom-small">
          <input type="text" onChange={(e) => setName(e.target.value)} required/>
        </div>
        <div className="add-list__button">
          <button onClick={toggleList} className="cancel">Cancel</button>
          <button className="cta" onClick={addList}>Confirm</button>
        </div>
      </div>
    </div>
  )
}

export default UpdateName;
