import {useContext, useState, useEffect} from "react"
import { GlobalContext } from "../../GlobalProvider"
// import Upload from "./Upload/Upload"
import New from "../Prospects/New"
import Upload from "../Prospects/Upload/Upload"
import AddFromList from "../Prospects/AddFromList"
import AddFromExisting from "../Prospects/AddFromExisting"
import { useParams } from "react-router";

function CampaignProspectAdd(){
  const {addProspect, newProspect, upload, addProspectCampaign, addExistingProspect, selectedCampaign} = useContext(GlobalContext)
  const [campaignProspectAdd, setCampaignProspectAdd] = addProspectCampaign
  const [addExistingModal, setAddExistingModal] = addExistingProspect
  const [newUpload, setNewUpload] = upload
  const [newP, setNewP] = newProspect
  const {id} = useParams()
  const [campaignId, setCampaignId] = selectedCampaign

  useEffect(() => {
    setter()
  },[])

  const setter = () => {
    if(id){
      setCampaignId(id)
      // console.log(id)
    }
    else{
      console.log(false)
    }
  };
  function toggleAdd(){
    setCampaignId("")
    setCampaignProspectAdd(!campaignProspectAdd)
  }

  function toggleNew(){
    setNewP(!newP)
  }

  function toggleUpload(){
    setNewUpload(!newUpload)
  }
  return(
    <div class="campaign-add-prospect">
      {newP===true ? <New/> : console.log('null')}
      {newUpload===true ? <Upload/> : console.log('null')}
      {/* {<AddFromList/>} */}
      {addExistingModal===true ? <AddFromExisting/> : null}
      <nav class="nav">
        <h1>Add Prospects</h1>
        <button onClick={toggleAdd} className="close">&times;</button>
      </nav>

      <div className="campaign-add-prospect__container">
        <h2>How would you like to add Prospects?</h2>

        <button onClick={toggleUpload} className="campaign-add-prospect__container--card">
          <i class="fas fa-file-csv"></i>
          <p>Upload CSV file</p>
          <i class="fas fa-arrow-right"></i>
        </button>
        <button onClick={toggleNew} className="campaign-add-prospect__container--card">
          <i class="fas fa-user"></i>
          <p>Create A New Prospect</p>
          <i class="fas fa-arrow-right"></i>
        </button>
        <button onClick={() => setAddExistingModal(!addExistingModal)} className="campaign-add-prospect__container--card">
          <i class="fas fa-users"></i>
          <p>Add Existing Prospect</p>
          <i class="fas fa-arrow-right"></i>
        </button>
        <button onClick={toggleNew} className="campaign-add-prospect__container--card">
          <i class="fas fa-address-book"></i>
          <p>Add From List</p>
          <i class="fas fa-arrow-right"></i>
        </button>
      </div>
    </div>
  )
}

export default CampaignProspectAdd
