const CampaignStatus = ({campaign, index}) => {
  const toggleDropdown = () => {
    let el = document.getElementsByClassName("status__dropdown--container")[index]
    if(el.style.display === "block"){
      el.style.display = "none"
    }
    else{
      el.style.display = "block"
    }
  }

  return(
    <div>
      {
        campaign.status === "Not Started" ? <p className="col-data copy__para--medium">{campaign.status}</p> : 
        <div className="status__dropdown">
          <button className={`btn-sec ${campaign.status === "Active" ? "active" : "paused"}`} onClick={() => toggleDropdown()}><i class="fas fa-circle"></i> <span>{campaign.status}</span><i class="fas fa-caret-down"></i></button>
          <div className="status__dropdown--container" style={{display: "none"}}>
            {
            campaign.status === "Active" ? 
              <p className="copy__para--big">Pause</p> : 
              <p className="copy__para--big">Resume</p>
            }
          </div>
        </div>
      }
    </div>
  )
}

export default CampaignStatus;
