import Sidebar from "../../../Components/Sidebar"
import Topbar from "../../../Components/Topbar"
import ProgressBar from "../../../Components/ProgressBar"
import { useContext, useState, useEffect } from "react"
import { GlobalContext } from "../../../GlobalProvider"
import EmailEditor from "../../../Components/Editor"
import {EditorContext} from "../../../EditorProvider"
import StepSettingsModal from "../../../Components/StepSettings/Modal"
import ToggleSwitch from "../../../Components/ToggleSwitch"

const CampaignSteps = (props) => {
  const [editorOpen, setEditorOpen] = useContext(EditorContext)
  const [modalOpen, setModalOpen] = useState(false)
  const {step} = useContext(GlobalContext)
  const [addStep, setAddStep] = step
  const [currentIndex, setCurrentIndex] = useState()
  const [emailAdded, setEmailAdded] = useState(false)
  const [daySetter, setDaySetter] = useState(0)
  const [stepSetter, setStepSetter] = useState(0)
  const [stepBtn, setBtn] = useState(true)

  useEffect(() => {
    newStep()
  }, [])

  function toggleEditor(index){
    setEditorOpen(!editorOpen)
    setCurrentIndex(index)
  }

  function toggleModal(){
    setModalOpen(!modalOpen)
  }

  function newStep(){
    const data = {
      stepNumber: stepSetter + 1,
      day: daySetter + 1,
      dayGap: daySetter + 1,
      hour: 0,
      hourGap: 0,
      minute: 0,
      minuteGap: 0,
      mails: [],
    }
    setAddStep(addStep => [...addStep, data]) 
    setDaySetter(daySetter + 1);
    setStepSetter(stepSetter + 1);
  }

  const handleCallback = (subject, email_content) => {
    const data = {
      subject: subject,
      email_content: email_content
    }
    let newArr = [...addStep]
    addStep[currentIndex].mails.push(data)
    setAddStep(newArr)
    checkr(currentIndex)
      setEmailAdded(!emailAdded)
  }

  function removeStep(index){
    const list = [...addStep];
    list.splice(index, 1);
    setDaySetter(daySetter - 1);
    setStepSetter(stepSetter - 1);
    list.forEach((e, i) => {
      e.stepNumber = i + 1
      e.day = i + 1
      e.dayGap = i + 1
    })
    console.log(list)
    setAddStep(list);
  }

  const checkr = (index) => {
    if (addStep[index].mails.length !== 0){
    }
    else{
    console.log(false)}
  }

  const addStepBetween = (index) => {
    const list = [...addStep];
    const data = {
      stepNumber: (index + 1) + 1,
      day: (index + 1) + 1,
      dayGap: (index + 1) + 1,
      hour: 0,
      hourGap: 0,
      minute: 0,
      minuteGap: 0,
      mails: [],
    }
    list.splice(index + 1, 0, data)
    list.forEach((e, i) => {
      e.stepNumber = i + 1
      e.day = i + 1
      e.dayGap = i + 1
    })
    setAddStep(list)
    setDaySetter(daySetter + 1);
    setStepSetter(stepSetter + 1);
  }

  return(
    <div class="campaign-create">
      <Sidebar/>
      {editorOpen===true ? <EmailEditor currentIndex={currentIndex} handleCallback={handleCallback}/> : console.log('null')}
      <Topbar title={"Campaigns"}/>
        <div className="campaign-create__container">
          <ProgressBar prevStep={props.prevStep} nextStep={props.nextStep} progress={props.progress}/>
        {addStep.map((d, index) => 
             <>
             <div className="timeline">

                <div key={index} className="campaign-create__container--step">

                  <div className="row-1">
                    <h3 key={index} className="title">Step {d.stepNumber}, Day {d.day}</h3>
                    <div className="settings">
                    {addStep[index].mails.length!==0 ? 
                      <button onClick={toggleModal}>Settings</button>
                      : null
                    }
                    </div>
                  </div>

                  <div className="row-2">
                    {addStep[index].mails.length===0 ? 
                      <div className="row-2__container">
                        <button onClick={() => toggleEditor(index)} className="mail-setter">Add Email</button> 
                          {index === 0 
                            ? 
                              null
                            : 
                              <button onClick={() => removeStep(index)} className="remove-step">Remove Step</button>
                          }
                      </div>
                    : 
                    <div className="row-2__container">
                      <div className="email">
                      <ToggleSwitch/>
                        <div className="email__info">
                          <p className="head">Email</p>
                          <p>subject: body</p>
                        </div>
                      </div>
                      <div className="options">
                          {index === 0 
                            ? 
                              null
                            : 
                              <button key={index} onClick={() => removeStep(index)} className="delete">Delete Step</button>
                          }
                      </div>
                    </div>
                    }
                  </div>

                  {/* <div className="row-3"> */}
                  {/*   <p className="test">+A/B Test</p> */}
                  {/* </div> */}
                </div>
                { modalOpen ? <StepSettingsModal toggler={toggleModal} index={index}/> : null}

                {addStep.length !== index + 1? <button onClick={() => addStepBetween(index)} className="add-sec"><i class="fas fa-plus-circle"></i></button> 
                  : 
                <button onClick={newStep} className="add"><i class="fas fa-plus"></i></button>
                }

             </div>
             </>
            )}
        </div>
    </div>
  )
}

export default  CampaignSteps
