import {NavLink} from "react-router-dom"
import CampaignsSidebar from "../../Components/Campaigns/CampaignsSidebar"
import Sidebar from "../../Components/Sidebar"
import Topbar from "../../Components/Topbar"
import {useNavigate, Link} from 'react-router-dom'
import {useState , useContext, useEffect} from "react"
import { GlobalContext } from "../../GlobalProvider"
import Add from "../../Components/Prospects/Add"
import Cookies from 'universal-cookie';
import axios from "axios"
import UpdateName from "../../Components/Campaigns/UpdateName"

const cookies = new Cookies()
function CampaignsHome(){
  const {addProspect, selectedCampaign, updateCampaign} = useContext(GlobalContext)
  const [add, setAdd] = addProspect
  const [showCampaigns, setShowCampaigns] = useState([])
  const [menu, setMenu] = useState(false)
  const [modal, setModal] = updateCampaign
  const [campaignId, setCampaignId] = selectedCampaign
  let navigate = useNavigate()

  useEffect(() => {
    getData()
  }, [])

  function toggleAdd(){
    setAdd(!add)
  }

  const openMenu = (index) => {
    setMenu(!menu)
    let el = document.getElementsByClassName("menu")[index]
    // ReactDOM.findDOMNode(instance)
    menu ? el.style.display = "block" : 
      el.style.display = "none"
  }

  const getData = async () => {
    const userId = cookies.get("userId")
    await axios.get(`/campaigns/${userId}`, {
      headers: {
        "Authorization": `Bearer ${cookies.get('access_token')}`
      }
    }).then((res) => {
      if(res.data === ""){
        console.log(true)
      }
      else{
       setShowCampaigns(res.data)
      }
    })
  }

  const deleteCampaign = (id) => {
    // console.log(id)
    axios.delete(`/campaigns/${id}`, {
      headers: {
        "Authorization": `Bearer ${cookies.get('access_token')}`
      }
    }).then((res) => {
      console.log(res)
    })
  }

  return(
    <div className="campaigns">
      {add===true ? <Add/> : console.log('null')}
      {modal ? <UpdateName/> : null}
      <Sidebar/>
      <Topbar title={"Campaigns"}/>
        <div className="campaigns__container">
          <div className="campaigns__container--sidebar">
            <CampaignsSidebar/>
          </div>

          <div className="campaigns__container--content">
            <div className="toolbar">
              <div className="name">
                <h4>My Campaigns</h4>
              </div>
              <div className="search">
                <input type="search" placeholder="Search"/>
                <button onClick={() => navigate('/campaigns/create')} className="cta">Add Campaigns</button>
              </div>
            </div>

            <div className="table__container">
              <table className="data">
                <thead>
                  <tr className="row1">
                    <th className="col-head">Status</th>
                    <th className="col-head">Name</th>
                    <th className="col-head">Scheduled Today</th>
                    <th className="col-head">Prospects</th>
                    <th className="col-head">Completed</th>
                  </tr>
                </thead>

                <tbody>
                  {showCampaigns.map((d, index) => 
                    <tr key={index}>
                      <td><p className="col-data">Not Started</p></td>
                      <td><NavLink to={`/campaigns/${d.id}/step`} >{d.campaign_name}</NavLink></td>
                      <td><p className="col-data">None</p></td>
                      <td><p className="col-data">{d.prospectCount}</p></td>
                      <td><p className="col-data">-</p></td>
                      <td>
                        <div className="campaign-edit-buttons">
                          <div>
                            <button className="cta-sec"><i className="fas fa-user-plus"></i></button>
                          </div>
                          <div className="menu-button">
                            <button className="cta-sec" onClick={() => openMenu(index)}><i className="fas fa-ellipsis-v"></i></button>
                            <div className="menu">
                              <div className="menu__content">
                                <div className="menu__content--option" onClick={() => {setCampaignId(d.id); setModal(!modal)}}><i className="fas fa-edit"></i>Edit Campaign Name</div>
                                <div className="menu__content--option"><i className="fas fa-play"></i>Start</div>
                                <div className="menu__content--option"><i className="fas fa-pause"></i>Pause</div>
                                <div className="menu__content--option" onClick={() => deleteCampaign(d.id)}><i className="fas fa-trash-alt"></i>Delete</div>
                              </div>
                            </div>
                          </div>
                        </div>
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
   </div>
  )
}

export default CampaignsHome
