import axios from "axios"
import Cookies from "universal-cookie"
import { useEffect, useState } from "react"

const cookies = new Cookies()
function Preview({selectedProspects, togglePreview, campaignId}) {
  const [previews, setPreviews] = useState([])
  const [selectedPreview, setSelectedPreview] = useState(0)
  const [selectedStep, setSelectedStep] = useState(1)

  useEffect(() => {
    campaignStart()
  }, [])

  const campaignStart = () => {
    const data = {
      selectedProspects: selectedProspects,
      selectedCampaign: campaignId
    }
    axios.post("/campaigns/generateProspectPreview", data, {
        headers: {
          "Authorization": `Bearer ${cookies.get('access_token')}`
        },
        params: {
          "userId": cookies.get("userId")
        }
    }).then((res) => {
      setPreviews(res.data)
    })
  }

  return(
    <div className="preview">
      <nav className="nav">
        <div className="col-1">
          <button onClick={togglePreview} className="btn-back"><i class="fas fa-chevron-left"></i></button>
          <h1 className="copy__para--big">Preview Campaign</h1>
        </div>
        <div className="col-2">
          <button className="btn" onClick={() => {console.log()}}>Start Campaign</button>
          <button onClick={null} className="btn-close">&times;</button>
        </div>
      </nav>

      <div className="preview__container">
        <div className="preview__container--item">
          <div className="header">
            <ul className="header__links">
              <li className="copy__para--big">Total Prospects: <strong>{previews.length}</strong></li>
            </ul>
          </div>

          {
            previews.map((d, index) => 
               <>
               <div className="selector" onClick={() => setSelectedPreview(index)}>
                  <p key={index} className="copy__para--medium">{d.prospectEmail}</p>
               </div>
               </>
            )
          }

        </div>

        <div className="preview__container--item">
          <div className="header">
            <ul className="header__links">
              {previews.length !== 0 ? previews[selectedPreview].previews.map((d, i) =>
                <li key={i} onClick={setSelectedStep(d.stepNumber)} className="header__links--link heading-4"><i class="fas fa-envelope"></i> Step: {d.stepNumber}</li>
              ) : null}
            </ul>
          </div>
          <p  className="copy__para--medium selector">{previews.length !== 0 ? previews[selectedPreview].prospectEmail : null}</p>
        </div>
      </div>
    </div>
  )
}

export default Preview;
