const CampaignStatus = ({campaign}) => {
  return(
    <div>
      {
        campaign.status === "Not Started" ? <p className="col-data copy__para--medium">{campaign.status}</p> : 
        <div className="status__dropdown">
          <button className={`btn-sec ${campaign.status === "Active" ? "active" : "paused"}`}><i class="fas fa-circle"></i> <span>{campaign.status}</span><i class="fas fa-caret-down"></i></button>
          <div className="status__dropdown--container">
            {
            campaign.status === "Active" ? 
              <p className="copy__para--big">Pause</p> : 
              <p className="copy__para--medium">Resume</p>
            }
          </div>
        </div>
      }
    </div>
  )
}

export default CampaignStatus;
