import axios from "axios"
import { useState, useEffect, useContext } from "react"
import Cookies from 'universal-cookie';
import { GlobalContext } from "../../GlobalProvider"

const cookies = new Cookies()
const WhichEmail = ({selectedEmail, setSelectedEmail}) => {
  const {step} = useContext(GlobalContext)
  const [addStep, setAddStep] = step
  const [creds, setCreds] = useState([])
  const userId = cookies.get("userId")

  useEffect(() => {
    getCreds()
  }, [])

  const headers = {
      "Authorization": `Bearer ${cookies.get('access_token')}`
  }

  const getCreds = () => {
    const params = {
      userId: userId
    }
    axios.get(`/creds/`, {
      params : params,
      headers: headers
    }).then((res) => {
      setCreds(res.data)
    })
  }

  return(
    <form action="">
      <select id="" name="" onChange={(e) => setSelectedEmail(e.target.value)}>
        <option value="none">None</option>
        {
          creds.map(d => 
            <option value={d.email}>{d.email}</option>
          )
        }
      </select>
    </form>
  )
}

export default WhichEmail
