import { useState } from 'react'
import {useNavigate, Link} from 'react-router-dom'
import axios from 'axios'
import {useCookies} from 'react-cookie'

function Login(){
  let navigate = useNavigate()
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [cookies, setCookies] = useCookies(["userId","auth", "isAuthenticated"])

  const handleCookie = (token, id) => {
    // setCookies("auth", token, {path: "/"})
    setCookies("userId", id, {path: "/", expires: new Date(Date.now() + (90*24*60*60*1000))})
  }

  const handleSubmit = async(e) => {
    e.preventDefault()
    const url = 'http://localhost:8080'
    const data = {
      username: username,
      password: password
    }
    //make axios post request
    await axios.post(`/login`, data, {
      headers: {
        "Content-Type": "application/json"
      }
    }).then((res) => {
      if(res.status === 200){
        console.log(res)
        console.log(res.data.access_token)
        handleCookie(res.data.access_token, res.data.user_id);
        var date = new Date();
        setCookies("isAuthenticated", true, {path: "/", expires: new Date(Date.now() + (90*24*60*60*1000))});
        navigate('/dashboard')
      }
    })
    }

  return(
    <div>
      <div className="login">
        <form className="login__form">
          <input type="text" onChange={(e) => setUsername(e.target.value)} placeholder="Username or Email"/>
          <input type="password" onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
          <button onClick={handleSubmit} className="login__form--submit">Login</button>
        </form>

        <div className="seprator">
          <div className="line"></div>
          <p>Or</p>
          <div className="line"></div>
        </div>

        <Link to="/register" className="cta-register">Register</Link>
      </div>
    </div>
  )
}

export default Login
