import axios from "axios"
import { useState, useEffect, useContext } from "react"
import { GlobalContext } from "../../GlobalProvider"
import Cookies from 'universal-cookie';
import {useParams} from "react-router"

const cookies = new Cookies()
function AddFromExisting() {
  const {addProspectCampaign, addExistingProspect, selectedCampaign} = useContext(GlobalContext)
  const [campaignProspectAdd, setCampaignProspectAdd] = addProspectCampaign
  const [addExistingModal, setAddExistingModal] = addExistingProspect
  const [prospects, setProspects] = useState([])
  const [selectedProspects, setSelectedProspects] = useState([])
  const [campaignId, setCampaignId] = selectedCampaign

  useEffect(() => {
    getListData()
  }, [])

  function toggleHome(){
    setAddExistingModal(!addExistingModal)
    setCampaignProspectAdd(!campaignProspectAdd)
  }

  const getListData = async () => {
    const userId = cookies.get("userId")
    const params = {
      userId: userId,
        campaignId: campaignId
    }
    await axios.get(`/prospects/prospect/not-in-campaign/`, {
      headers: {
        "Authorization": `Bearer ${cookies.get('access_token')}`
      },
      params: params
    }).then((res) => {
      if(res.data === ""){
        console.log("Data Null")
      }
      else{
        console.log(res.data)
        setProspects(res.data)
      }
    })
  }

  const addToCampaign = async () => {
    const data = []
    for(let i=0; i < selectedProspects.length; i++){
      data.push({"id": selectedProspects[i]})
    }
    console.log(data);
    axios.put("/prospects/prospect/addToCampaign", data, {
      headers:{
        "Authorization": `Bearer ${cookies.get('access_token')}`
      },
      params: {
        campaignId: campaignId
      }
    }).then((res) => {
      if(res.status === 200){
        window.location.reload()
      }
    })
  }

  const handleSelectProspect = (e) => {
    const prospectId = e.target.value
    if(!selectedProspects.includes(prospectId)){
      setSelectedProspects([...selectedProspects, prospectId])
    }
    else{
      setSelectedProspects(selectedProspects.filter((selectedProspectsId) => {
        return selectedProspectsId !== prospectId;
      })
                          )
    }
  }

  const handleSelectAllProspects = () => {
    if(selectedProspects.length < prospects.length){
      setSelectedProspects(prospects.map((d, index) => d.id))
    }
    else{
      setSelectedProspects([]);
    }
  }

  return(
    <div class="add-from-existing">
      <nav class="nav">
        <div className="col-1">
          <button onClick={() => setAddExistingModal(!addExistingModal)} className="back"><i class="fas fa-chevron-left"></i></button>
          <h1>Add Prospects To Campaign</h1>
        </div>
        <div className="col-2">
          <button className="cta" onClick={addToCampaign}>Add to campaign</button>
          <button onClick={toggleHome} className="close">&times;</button>
        </div>
      </nav>

      <div className="add-from-existing__container">
            <div className="toolbar">
              <div className="name">
                <h4>My Prospects</h4>
              </div>
              <div className="search">
                <input type="search" placeholder="Search"/>
              </div>
            </div>

            <div className="table__container">
              <table className="data">
                <thead>
                  <tr className="row1">
                    <input className="col-head" type="checkbox" checked={selectedProspects.length === prospects.length} onChange={handleSelectAllProspects}/>
                    <th className="col-head">Prospect</th>
                    <th className="col-head">Contact</th>
                    <th className="col-head">Company</th>
                    <th className="col-head">List</th>
                    <th className="col-head">Tags</th>
                    <th className="col-head">Status</th>
                  </tr>
                </thead>

                <tbody>
                  {prospects.map(d => 
                    <tr key={d.id}>
                      <input type="checkbox" value={d.id} checked={selectedProspects.includes(d.id)} onChange={handleSelectProspect}/>
                      <td><p className="col-data">{d.firstName} {d.lastName}</p></td>
                      <td><p className="col-data">
                            {d.prospectLinkedinUrl}
                            {d.prospectMobileNumber}
                            {d.prospectEmail}
                      </p></td>
                      <td><p className="col-data">{d.prospectCompany}</p></td>
                      <td><p className="col-data">Campaign 1</p></td>
                      <td><p className="col-data">-</p></td>
                      <td><p className="col-data">Not Contacted</p></td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
      </div>
  )
}

export default AddFromExisting
