import React, {Component} from "react"
import {EditorState, convertToRaw} from 'draft-js' 
import '../../node_modules/react-draft-wysiwyg/dist/react-draft-wysiwyg.css'
import draftToHtml from "draftjs-to-html"
import { EditorContext } from "../EditorProvider"
import { Editor } from '@tinymce/tinymce-react'; 

export default class EmailEditor extends Component{
  static contextType = EditorContext

  state = {
    editorState: EditorState.createEmpty(),
    subject: '',
    email_content: ''
  }

  onEditorStateChange = (editorState) => {
    this.setState({editorState})
  }

    handleEditorChange = (e) => {
        console.log('e',e);
        console.log('Content was updated:', e.target.getContent());
    }



  render(){
    const {editorState} = this.state;
    const data = draftToHtml(convertToRaw(editorState.getCurrentContent()))
    const [editorOpen, setEditorOpen] = this.context

    const toggleEditor = () => {
      setEditorOpen(!editorOpen)
    }

    const lp = () => {
      this.props.handleCallback(this.state.subject, data)
      setEditorOpen(!editorOpen)
    }

    return(
    <div class="editor">
      <div className="editor__modal">
        <div className="editor__modal--row-1">
          <h2 className="title">Add Email</h2>
          <button onClick={toggleEditor}>&times;</button>
        </div>

        <div className="editor__modal--row-2">
          <p>Subject</p>
          <input onChange={(e) => this.setState({subject: e.target.value})} type="text"/>
          <p className="placeholder">Add Placeholder</p>
        </div>

        <div className="editor__modal--row-3">
          <Editor
            apiKey="fnajkiw2wcz5d1lrgsif3xicw1wdvcj4f9hmzxrngbljd9b6"
            initialValue="<p>Initial content</p>"
            init={{
              height: 500,
              menubar: false,
              plugins: [
                'advlist underline autolink lists link image tinydrive', 
                'charmap print preview anchor help forecolor',
                'searchreplace visualblocks code emoticons link',
                'insertdatetime media table paste wordcount table fontselect'
              ],
              toolbar_mode: "wrap",
              toolbar:
                'fontselect fontsizeselect forecolor  \
                | bold italic underline \
                | alignleft aligncenter alignright alignjustify| \
                bullist numlist outdent indent \
                | table link image code \
                | emoticons ',
              image_title: true,
              automatic_uploads: true,
              paste_data_images: true,
              statusbar: false,
              theme_advanced_toolbar_location: "bottom",
            }}
            onChange={this.handleEditorChange}
          />
        </div>

        <div className="editor__modal--row-4">
          <button onClick={lp} className="cta">Add Email</button>
        </div>
      </div>
    </div>
    )
  }
}
