import {useState, useContext} from 'react'
import Sidebar from '../../../Components/Sidebar'
import Topbar from '../../../Components/Topbar'
import CampaignSteps from './CampaignSteps'
import CampaignSettings from './CampaignSettings'
import AddProspect from './AddProspect'
import { GlobalContext } from '../../../GlobalProvider'

function Create(){
  const {selectedCampaign} = useContext(GlobalContext)
  const [progress, setProgress] = useState(1)
  const [campaignId, setCampaignId] = selectedCampaign

  const prevStep = () => {
    setProgress(progress - 1)
  }

  const nextStep = () => {
    setProgress(progress + 1)
  }

  switch(progress){
    case 1: 
      return(
        <CampaignSteps nextStep={nextStep} progress={progress} prevStep={prevStep}/>
      )
    case 2:
      return(
        <CampaignSettings nextStep={nextStep} progress={progress} prevStep={prevStep} setCampaignId={setCampaignId}/>
      )
    case 3:
      return(
        <AddProspect nextStep={nextStep} progress={progress} prevStep={prevStep} campaignId={campaignId}/>
      )
    default:
      return null
  }
}

export default Create
