import axios from "axios";
import AppSidebar from "../../../Components/Sidebars/AppSidebar";
import AppTopbar from "../../../Components/Topbars/AppTopbar"
import CampaignSidebar from "../../../Components/Sidebars/CampaignSidebar"
import SingleCampaignNavbar from "../../../Components/CampaignComponents/SingleCampaignNavbar";
import CampaignAdd from "../../../Components/CampaignComponents/CampaignProspects/CampaignAdd"
import {useState, useEffect} from "react";
import { useParams } from "react-router";
import Cookies from 'universal-cookie';
import ToggleSwitch from "../../../Components/ToggleSwitch";
import { GlobalContext } from "../../../GlobalProvider";
import { useContext } from "react";

const cookies = new Cookies()
function SingleCampaignSteps() {
  const [steps, setSteps] = useState([])
  const { id } = useParams()
  const {addProspect, newProspect, upload, addProspectCampaign} = useContext(GlobalContext)
  const [campaignProspectAdd, setCampaignProspectAdd] = addProspectCampaign

  useEffect(() => {
    getSteps()
  }, [])

  const headers = {
      "Authorization": `Bearer ${cookies.get('access_token')}`
  }

  const getSteps = () => {
    axios.get(`/steps/${id}`, {
      headers: headers
    }).then((res) => {
      setSteps(steps => [...steps, res.data])
      for(let i=0; i < steps.length; i++){
        console.log(steps[i][0].id)
      }
    })
  }

  return(
    <div className="single-campaign">
      {campaignProspectAdd===true ? <CampaignAdd/> : console.log('null')}
      <AppSidebar/>
      <AppTopbar title={"Campaign"}/>
      <div className="single-campaign__container">
          <div className="single-campaign__container--sidebar">
            <CampaignSidebar/>
          </div>
          <div className="single-campaign__container--content">
            <SingleCampaignNavbar/>
            {steps.map((d, index) => 
               d.map((e, i) => 

                <div key={index} className="single-campaign__container--content--steps">
                  <div className="row-1">
                    <div className="row-1__left">
                      <h3 className="heading-2">Step {e.stepNumber}, Day {e.day}</h3>
                    </div>
                    <div className="settings">
                      <div className="row-1__right">
                        <div className="row-1__right--item"><p>Assigned to: {e.whichEmail}</p></div>
                        <div className="row-1__right--item">
                          {
                            e.days.map( (d, i) => 
                              <p key={i} className="days">{d}</p>
                           )
                          }
                        </div>

                      <button onClick={() => {return null}} className="btn-tertiary row-1__right--item">Step Settings</button>
                      </div>
                    </div>
                  </div>

                  <div className="row-2">
                    <div className="row-2__container">
                      <div className="email">
                      <ToggleSwitch/>
                        <div className="email__info">
                          <p className="head">Email</p>
                          <p>subject: body</p>
                        </div>
                      </div>
                      <div className="options">
                        <button key={index} onClick={() => console.log("ff")} className="delete">Delete Step</button>
                      </div>
                    </div>
                  </div>

                  <div className="row-3">
                    <button>+A/B Test</button>
                    <button>Remove Step</button>
                  </div>
                </div>
              )
            )}
          </div>
      </div>
    </div>
  )
}

export default SingleCampaignSteps;
