import {useState , useContext} from "react"
import { GlobalContext } from "../../GlobalProvider";
import axios from "axios"
import Cookies from 'universal-cookie';

const cookies = new Cookies();
function CreateList({toggleCreateList}){
  const {createList} = useContext(GlobalContext)
  const [list, setList] = createList
  const [name, setName] = useState('')

  const addList = () => {
    const userId = cookies.get("userId")
    const token = cookies.get("access_token")
    const data = {
      name: name
    }
    axios.post(`/prospects/prospect_lists/${userId}`, data, {
      headers:{
        "Authorization": `Bearer ${token}`
      }
    }).then((res) => {
      if(res.status === 201){
        toggleCreateList()
      }
    })
  }

  return(
    <div className="add-list">
      <div className="add-list__card">
        <h1 className="">Create List</h1>
        <div className="add-list__form">
          <input type="text" placeholder="List Name" onChange={(e) => setName(e.target.value)} required/>
        </div>
        <div className="add-list__button">
          <button onClick={() => toggleCreateList()} className="btn-sec">Cancel</button>
          <button className="btn" onClick={addList}>Confirm</button>
        </div>
      </div>
    </div>
  )
}

export default CreateList
